trait Snowflake
{
    protected static function boot()
    {
        parent::boot();

        static::creating(function($model) {
            if (! $model->getKey()) {
               $key = resolve('snowflake')->id();
               $model->{$model->getKeyName()} = $key;
            }
        });
    }

    public function getIncrementing()
    {
        return false;
    }
}

